INSERT INTO articles (law_id, article_number, title, text) VALUES
(20, '1', 'Άρθρο 1', 'Ο ποινικός κώδικας καθορίζει τις πράξεις που αποτελούν αξιόποινες και τις ποινές που επιβάλλονται για αυτές.'),
(20, '2', 'Άρθρο 2', 'Κανείς δεν τιμωρείται για πράξη που δεν έχει οριστεί από τον νόμο ως αξιόποινη.'),
(20, '3', 'Άρθρο 3', 'Ο νόμος που ορίζει ως αξιόποινη μια πράξη και επιβάλλει ποινή για αυτήν εφαρμόζεται από την ημέρα που δημοσιεύεται στην Εφημερίδα της Κυβερνήσεως.'),
(20, '4', 'Άρθρο 4', 'Ο νόμος που καταργεί την ποινικότητα μιας πράξης ή μειώνει την ποινή που προβλέπει εφαρμόζεται και για τις πράξεις που έγιναν πριν από την έναρξη της ισχύος του.'),
(20, '5', 'Άρθρο 5', 'Ο νόμος που αυξάνει την ποινή που προβλέπει για μια πράξη δεν εφαρμόζεται για τις πράξεις που έγιναν πριν από την έναρξη της ισχύος του.'),
(20, '6', 'Άρθρο 6', 'Ο νόμος που ορίζει ως αξιόποινη μια πράξη και επιβάλλει ποινή για αυτήν εφαρμόζεται και για τις πράξεις που έγιναν στο εξωτερικό, αν η πράξη είναι αξιόποινη και κατά το νόμο της χώρας όπου έγινε.'),
(20, '7', 'Άρθρο 7', 'Ο νόμος που ορίζει ως αξιόποινη μια πράξη και επιβάλλει ποινή για αυτήν εφαρμόζεται και για τις πράξεις που έγιναν στο εξωτερικό, αν η πράξη είναι αξιόποινη και κατά το νόμο της χώρας όπου έγινε και ο υπαίτιος βρίσκεται στην Ελλάδα.'),
(20, '8', 'Άρθρο 8', 'Ο νόμος που ορίζει ως αξιόποινη μια πράξη και επιβάλλει ποινή για αυτήν εφαρμόζεται και για τις πράξεις που έγιναν στο εξωτερικό, αν η πράξη είναι αξιόποινη και κατά το νόμο της χώρας όπου έγινε και ο υπαίτιος είναι Έλληνας πολίτης.'),
(20, '9', 'Άρθρο 9', 'Ο νόμος που ορίζει ως αξιόποινη μια πράξη και επιβάλλει ποινή για αυτήν εφαρμόζεται και για τις πράξεις που έγιναν στο εξωτερικό, αν η πράξη είναι αξιόποινη και κατά το νόμο της χώρας όπου έγινε και ο υπαίτιος είναι αλλοδαπός που βρίσκεται στην Ελλάδα.'),
(20, '10', 'Άρθρο 10', 'Ο νόμος που ορίζει ως αξιόποινη μια πράξη και επιβάλλει ποινή για αυτήν εφαρμόζεται και για τις πράξεις που έγιναν στο εξωτερικό, αν η πράξη είναι αξιόποινη και κατά το νόμο της χώρας όπου έγινε και ο υπαίτιος είναι αλλοδαπός που βρίσκεται στην Ελλάδα και η πράξη αφορά την ασφάλεια του κράτους ή την οικονομική του ζωή.'); 