-- Άρθρο 110 - Διαδικασία για τη χορήγηση και την ανάκληση της απόλυσης
INSERT INTO penal_code_articles (article_number, article_title, article_content, creation_date, update_date)
VALUES (
    '110',
    'Διαδικασία για τη χορήγηση και την ανάκληση της απόλυσης',
    'Για τη χορήγηση και την ανάκληση της απόλυσης υπό όρο αποφασίζει το συμβούλιο των πλημμελειοδικών του τόπου της έκτισης της ποινής. Ο κατάδικος δεν κλητεύεται ούτε εμφανίζεται ενώπιον του συμβουλίου και μπορεί, αφού λάβει γνώση, μέσω του διευθυντή του καταστήματος κράτησης, της πρότασης του εισαγγελέα, να υποβάλει έγγραφο υπόμνημα.',
