-- Άρθρο 111 - Χρόνος παραγραφής των εγκλημάτων
INSERT INTO penal_code_articles (article_number, article_title, article_content, creation_date, update_date)
VALUES (
    '111',
    'Χρόνος παραγραφής των εγκλημάτων',
    'Το αξιόποινο εξαλείφεται με την παραγραφή. Τα κακουργήματα παραγράφονται: α) μετά είκοσι έτη, αν ο νόμος προβλέπει γι αυτά την ποινή του θανάτου ή της ισόβιας κάθειρξης, β) μετά δεκαπέντε έτη, σε κάθε άλλη περίπτωση. Τα πλημμελήματα παραγράφονται μετά πέντε έτη. Τα πταίσματα παραγράφονται μετά δύο έτη.',
